(5568, 224, '\n<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Produksi massal merupakan nama yang diberikan kepada metode produksi dengan jumlah besar dengan biaya rendah per unitnya. Sebelum memproduksi produk hal yang diperhatikan dalam produksi massal adalah...</span></span></span></p>\r\n\n', 1, '', '\n<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Pemilihan bahan baku produk</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Penjadwalan waktu produksi</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Pemilihan mesin</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Mobilisasi mesin produksi</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Pembayaran gaji karyawan</span></span></span></p>\n', 'opt1_soal', '\n\n', 1, 1),
(5569, 224, '\n<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Rancangan awal desain suatu produk memiliki beberapa tujuan yaitu...</span></span></span></p>\r\n\n', 1, '', '\n<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Mengetahui manfaat produk dan fungsi barang</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Mengetahui bentuk produk</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Menghitung estimasi biaya</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Menentukan alat produksi</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Menyeleksi karyawan produksi</span></span></span></p>\n', 'opt1_soal', '\n\n', 1, 1);
