(5702, 216, '\n\n<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Apa yang dimaksud dengan metode perakitan yang dapat di tukar-tukar &hellip;..</span></span></span></p>\r\n\n\n', 1, '', '\n\n<p><span style="font-size:11.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Bagian-bagian yang akan dirakit dapat di tukarkan satu sama lain</span></span></p>\n\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Salah satu bentuk yang rumit,dan sulit dirakit</span></span></span></p>\n\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Bagian yang sudah distandarkan dengan baik</span></span></span></p>\n\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Perakitan dengan metode pemilihan</span></span></span></p>\n\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Merupakan tahap implementasi</span></span></span></p>\n\n', 'opt1_soal', '\n\n\n\n', 1, 1),
