(6755, 186, '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">Perhatikan gambar berikut !</span></span></p>\r\n\r\n<p style="text-align:justify"><img id="" src="/pat/js/kcfinder/upload/images/FIS-NONTIK/5.png" style="height:32px; width:283px" /></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">Gambar tersebut menunjukan 3 buah magnet batang. Jika C kutub utara, B dengan C tolak menolak, serta D dengan E tarik menarik, maka jenis kutub magnet pada A dan F adalah . . . </span></span></p>\r\n', 1, '', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">A kutub utara, F kutub utara</span></span></p>\r\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">A kutub selatan, F kutub utara</span></span></p>\r\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">A kutub utara, F kutub selatan</span></span></p>\r\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">A kutub selatan, F kutub selatan</span></span></p>\r\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">A kutub utara, F kutub utara</span></span></p>\r\n', 'opt4_soal', '', 1, 1),
(6756, 186, '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Penyebab jarum kompas yang selalu menunjukan arah utara-selatan adalah . . .</span></span></span></p>\r\n\n', 1, '', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Di utara dan di selatan ada magnet yang terbesar</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Untuk memudahkan penentuan arah mata angina</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Ada tarikan dari kutub-kutub bumi</span></span></span></p>\n', '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Ada tarikan oleh kutub-kutub magnet bumi</span></span></span></p>\n', '<p><span style="font-size:11.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Karena kompas hanya bisa menunjuk arah utara dan selatan saja</span></span></p>\n', 'opt4_soal', '\n\n', 1, 1);
INSERT INTO `tbl_soal` (`id_soal`, `id_bank_soal`, `des_soal`, `jenis_soal`, `audio_soal`, `opt1_soal`, `opt2_soal`, `opt3_soal`, `opt4_soal`, `opt5_soal`, `kunciopt_soal`, `kunciessay_soal`, `acak_soal`, `acak_opsi`) VALUES
(6757, 186, '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">Perhatikan gambar berikut !</span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><img id="" src="/pat/js/kcfinder/upload/images/FIS-NONTIK/7.png" style="height:151px; width:56px" /></span></span></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">Jika kedua klip tersebut memiliki sifat magnet setelah ditempelkan pada magnet, maka kutub-kutub klip PQ dan klip RS adalah . . .</span></span></p>\r\n', 1, '', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">P = selatan, Q = utara, R = selatan, S = utara</span></span></p>\r\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">P = utara, Q = selatan, R = utara, S = selatan </span></span></p>\r\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">P = selatan, Q = selatan, R = utara, S = selatan</span></span></p>\r\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">P = utara, Q = selatan, R = selatan, S = utara</span></span></p>\r\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">P = selatan, Q = utara, R = utara, S = selatan</span></span></p>\r\n', 'opt2_soal', '', 1, 1),
(6758, 186, '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">Perhatikan kedua magnet berikut !</span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><img id="" src="/pat/js/kcfinder/upload/images/FIS-NONTIK/8.png" style="height:39px; width:221px" /></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">Jika kutub P adalah kutub utara dan jika kutub Q dan kutub R didekatkan terjadi tolak menolak, pernyataan yang tepat adalah . . . </span></span></p>\r\n', 1, '', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">Q kutub utara dan R kutub utara</span></span></p>\r\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">Q kutub selatan dan R kutub utara</span></span></p>\r\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">Q kutub selatan dan R kutub selatan</span></span></p>\r\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">P kutub utara dan S kutub selatan</span></span></p>\r\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">P kutub utara dan R kutub selatan</span></span></p>\r\n', 'opt3_soal', '', 1, 1),
(6759, 186, '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Sebuah besi didekatkan pada sebuah magnet. Besi tersebut menjadi magnet sehingga dapat menarik serbuk besi di dekatnya. Pernyataan tersebut merupakan pembuatan magnet dengan menggunakan cara&nbsp; . . .</span></span></span></p>\r\n\n', 1, '', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">gosokan</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">arus listrik</span></span></span></p>\n', '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">induksi</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">elektromagnetik</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">arus searah</span></span></span></p>\n', 'opt3_soal', '\n\n', 1, 1),
(6760, 186, '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Sifat kemagnetan sebuah magnet tidak akan hilang jika magnet tersebut . . . </span></span></span></p>\r\n\n', 1, '', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">dipanaskan</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">dialiri arus listrik bolak balik</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">dipukul-pukul</span></span></span></p>\n', '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">digosokan pada batang besi</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">dilelehkan</span></span></span></p>\n', 'opt4_soal', '\n\n', 1, 1),
(6761, 186, '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Arah gaya pada kutub batang magnet yang benar adalah . . .</span></span></span></p>\r\n\n', 1, '', '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">keluar dari kutub utara dan masuk dari kutub selatan</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">keluar dari kutub selatan dan masuk dari kutub utara</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">keluar dari kutub utara dan selatan </span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">masuk dari kutub utara dan selatan</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">masuk dari kutub utara dan keluar dari kutub selatan</span></span></span></p>\n', 'opt1_soal', '\n\n', 1, 1),
(6762, 186, '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Daerah magnet yang mempunyai gaya tarik atau gaya tolak terbesar disebut sebagai . . .</span></span></span></p>\r\n\n', 1, '', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">induksi magnet</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">medan magnet</span></span></span></p>\n', '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">kutub magnet</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">kontruksi magnet</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">garis gaya magnetik</span></span></span></p>\n', 'opt3_soal', '\n\n', 1, 1),
(6763, 186, '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Seperti halnya listrik magnet juga dapat menimbulkan suatu medan. Daerah sekitar magnet yang masih dipengaruhi oleh gaya magnet disebut . . .</span></span></span></p>\r\n\n', 1, '', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">garis gaya magnet</span></span></span></p>\n', '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">medan magnet</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">kutub magnet</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">arah arus magnet</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">gaya tarik magnet</span></span></span></p>\n', 'opt2_soal', '\n\n', 1, 1),
(6764, 186, '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Medan magnet dapat digambarkan sebagai garis-garis khayal yang disebut garis gaya. Sifat dari gaya gerak magnet yang benar adalah . . .</span></span></span></p>\r\n\n', 1, '', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">garis gaya magnet rapat, medan magnetnya lemah</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">garis gaya magnet renggang, medan magnetnya kuat</span></span></span></p>\n', '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">garis gaya magnet rapat, medan magnetnya kuat</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">garis gaya magnet renggang, tidak ada magnet</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">garis gaya magnet renggang, terdapat medan magnet</span></span></span></p>\n', 'opt3_soal', '\n\n', 1, 1),
(6765, 186, '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Seorang sarjana dari Denmark&nbsp; yang bernama Han Christian Oersted berhasil menemukan bahwa . . .</span></span></span></p>\r\n\n', 1, '', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Di sekitar medan magnet terdapat listrik</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Di sekitar medan magnet terdapat gaya</span></span></span></p>\n', '<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Di sekitar listrik terdapat medan listrik</span></span></span></p>\n', '\n<p style="text-align:justify"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Di sekitar kawat berarus listrik terdapat medan magnet</span></span></span></p>\n', '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Di sekitar magnet terdapat kuat arus listrik</span></span></span></p>\n', 'opt4_soal', '\n\n', 1, 1),
